const Article = () => {
  const { articleId } = useParams();

  const { data, isLoading, isError } = useQuery(
    ["article", articleId],
    () => {
      return new Promise((resolve, reject) => {
        setTimeout(() => {
          resolve({ fakeArticleDetails, fakeArticleContent });
        }, 1000);
      });
    },
    {
      select: (data) => {
        data.fakeArticleContent.sort((obj1, obj2) => {
          if (obj1.order < obj2.order) {
            return -1;
          } else {
            return 1;
          }
        });
        return data;
      },
    }
  );

  const renderArticle = () => {
    return data.fakeArticleContent.map((articleContent) => {
      if (articleContent.htmlTag == "h1") {
        return (
          <h1 className={style["article-title"]}>
            {articleContent.htmlContent}
          </h1>
        );
      } else if (articleContent.htmlTag == "h2") {
        return (
          <h2 className={style["article-secondary-title"]}>
            {articleContent.htmlContent}
          </h2>
        );
      } else if (articleContent.htmlTag == "p") {
        return (
          <p className={style["article-paragraph"]}>
            {articleContent.htmlContent}
          </p>
        );
      } else if (articleContent.htmlTag == "img") {
        return (
          <img className={style["article-img"]} src={articleContent.imageScr} />
        );
      } else if (articleContent.htmlTag == "underline") {
        return <div className={style["underline"]}></div>;
      } else {
        return (
          <CodeEditor
            value={articleContent.codecontent}
            language="js"
            readOnly={true}
            padding={30}
            style={{
              flexShrink: 0,
              fontSize: window.innerWidth < "500" ? 15: 20,
              backgroundColor: "#202124",
              fontFamily:
                "ui-monospace,SFMono-Regular,SF Mono,Consolas,Liberation Mono,Menlo,monospace",
            }}
          />
        );
      }
    });
  };

  return (
    <div className={style['article-parent-container']}>
      {isLoading == true ? (
        <Loading  message="Please Wait , while we are fetching data" />
      ) : ( 
        <div className={style['article-container']}>
          <div className={style["article-metadata"]}>
            <div className={style["article-metadata-left"]}>
              <img
                className={style["article-metadata-profileImg"]}
                src={data.fakeArticleDetails.creatorImageUrl}
              />
              <p className={style["article-metadata-name"]}>
                {data.fakeArticleDetails.creatorName}
              </p>
              <p className={style["article-metadata-timestamp"]}>
                {data.fakeArticleDetails.timestamp}
              </p>
            </div>
            <div className={style["article-metadata-right"]}>
              <a href="">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                >
                  <path d="M14.829 6.302c-.738-.034-.96-.04-2.829-.04s-2.09.007-2.828.04c-1.899.087-2.783.986-2.87 2.87-.033.738-.041.959-.041 2.828s.008 2.09.041 2.829c.087 1.879.967 2.783 2.87 2.87.737.033.959.041 2.828.041 1.87 0 2.091-.007 2.829-.041 1.899-.086 2.782-.988 2.87-2.87.033-.738.04-.96.04-2.829s-.007-2.09-.04-2.828c-.088-1.883-.973-2.783-2.87-2.87zm-2.829 9.293c-1.985 0-3.595-1.609-3.595-3.595 0-1.985 1.61-3.594 3.595-3.594s3.595 1.609 3.595 3.594c0 1.985-1.61 3.595-3.595 3.595zm3.737-6.491c-.464 0-.84-.376-.84-.84 0-.464.376-.84.84-.84.464 0 .84.376.84.84 0 .463-.376.84-.84.84zm-1.404 2.896c0 1.289-1.045 2.333-2.333 2.333s-2.333-1.044-2.333-2.333c0-1.289 1.045-2.333 2.333-2.333s2.333 1.044 2.333 2.333zm-2.333-12c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm6.958 14.886c-.115 2.545-1.532 3.955-4.071 4.072-.747.034-.986.042-2.887.042s-2.139-.008-2.886-.042c-2.544-.117-3.955-1.529-4.072-4.072-.034-.746-.042-.985-.042-2.886 0-1.901.008-2.139.042-2.886.117-2.544 1.529-3.955 4.072-4.071.747-.035.985-.043 2.886-.043s2.14.008 2.887.043c2.545.117 3.957 1.532 4.071 4.071.034.747.042.985.042 2.886 0 1.901-.008 2.14-.042 2.886z" />
                </svg>{" "}
              </a>
              <a href="">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                >
                  <path d="M12 0c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm6.066 9.645c.183 4.04-2.83 8.544-8.164 8.544-1.622 0-3.131-.476-4.402-1.291 1.524.18 3.045-.244 4.252-1.189-1.256-.023-2.317-.854-2.684-1.995.451.086.895.061 1.298-.049-1.381-.278-2.335-1.522-2.304-2.853.388.215.83.344 1.301.359-1.279-.855-1.641-2.544-.889-3.835 1.416 1.738 3.533 2.881 5.92 3.001-.419-1.796.944-3.527 2.799-3.527.825 0 1.572.349 2.096.907.654-.128 1.27-.368 1.824-.697-.215.671-.67 1.233-1.263 1.589.581-.07 1.135-.224 1.649-.453-.384.578-.87 1.084-1.433 1.489z" />
                </svg>
              </a>
              <a href="">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="24"
                  height="24"
                  viewBox="0 0 24 24"
                >
                  <path d="M12 0c-6.627 0-12 5.373-12 12s5.373 12 12 12 12-5.373 12-12-5.373-12-12-12zm-2 16h-2v-6h2v6zm-1-6.891c-.607 0-1.1-.496-1.1-1.109 0-.612.492-1.109 1.1-1.109s1.1.497 1.1 1.109c0 .613-.493 1.109-1.1 1.109zm8 6.891h-1.998v-2.861c0-1.881-2.002-1.722-2.002 0v2.861h-2v-6h2v1.093c.872-1.616 4-1.736 4 1.548v3.359z" />
                </svg>
              </a>
            </div>
          </div>

          <div className={style["article-content"]}>{renderArticle()}</div>
        </div>
      )}
    </div>
  );
};

export default Article;
